import React, { Component } from 'react';
import axios from 'axios';


export class API extends Component {
    constructor(props) {
        super(props);
        this.state = {value: ''};
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }
  state = {
    hash:null,
    value:0

  };

  sendOTP(){
    var jsonPhone = JSON.stringify({
        "phone":"+19738794337",
        "hash":this.state.hash,
        "otp":this.state.value

        });
    axios.post('http://localhost:3000/verifyOTP', jsonPhone,{
        headers: {'Content-Type': 'application/json'}})
      .then(res => {
        const res_data = res.data;
        console.log(res_data)
      })
  }

  handleChange(e){
      this.setState({value:e.target.value})
  }

  txtOTP(){

    var jsonPhone = JSON.stringify({
        "phone":"+19738794337"

        });

      axios.post('http://localhost:3000/sendOTP', jsonPhone,{
        headers: {'Content-Type': 'application/json'}})
      .then(res => {
        const res_data = res.data;
        this.setState({hash:res_data.hash})
        console.log(res_data)
      })
  }
  componentDidMount() {



  }


  render() {
    return (<div>
      <input
            type="text"
            value={this.state.value}
            onChange={this.handleChange}
         />
         <button onClick={this.sendOTP}>Default</button>;
         <button onClick={this.txtOTP}>send</button>;

    </div>

    );
  }
}
export default API
