import React, { Component } from 'react';
import axios from 'axios';
import { Redirect } from "react-router-dom";
import SubmitPhone from '../components/SubmitPhone.jsx';

export class API extends Component {



  state = {
    hash:null,
    phone:"+19738790000",
    otp:0

  };



  sendOTP(){
    var jsonPhone = ({
        phone:this.state.phone,
        hash:this.state.hash,
        otp:this.state.value

    });
    var url;
        if(process.env.NODE_ENV=='production')
            url='';
        else
            url='http://localhost:3000'
    axios.post(url+'/verifyOTP', jsonPhone,{ withCredentials: true },{
        headers: {'Content-Type': 'application/json'}})
      .then(res => {
        const res_data = res.data;
        console.log(res_data)
        console.log(!res_data.registered)

        if(!res_data.registered)
        window.location.href='/register';

      })
  }

  handleChange(e){
      this.setState({phone:e.target.value})
  }

  txtOTP(){

    var jsonPhone = ({
        phone:'+19738790000'
        });
        var url;
        if(process.env.NODE_ENV=='production')
            url='';
        else
            url='http://localhost:3000'
      axios.post(url+'/sendOTP',jsonPhone,{ withCredentials: true }, {
        headers: {'Content-Type': 'application/json'}})
      .then(res => {
        const res_data = res.data;
        this.setState({hash:res_data.hash})
        if (res_data.otp)
            this.setState({value:res_data.otp})
        console.log(res_data)
      })
  }
  componentDidMount() {



  }


  render() {
    return (<div> 
        <SubmitPhone></SubmitPhone>
    </div>

    );
  }
}
export default API
