import React, { Component } from 'react';
import {Calendar, CalendarList, Agenda} from 'react-native-calendars';
import { ParticipantConversationPage } from 'twilio/lib/rest/conversations/v1/participantConversation';



export default function EatingCalendar(props) {



function getDayData(d) {
  var ctr = 0;
  while (ctr<props.eating_data.length){
    if(props.eating_data[ctr].date===d.dateString){
      return props.eating_data[ctr].activity_data.alcoholDrinksHad;
    }
    ctr++;
  }
  return 'empty';
}

function updateDay(value,date){
  console.log(value+' '+date);
}

function editDay(d){
  console.log(d);
  props.setDrawerContent(<form>
          <input type="text" name="name" />
          <button onClick={(e)=>{updateDay(e.value,d.dateString)}}>Submit</button>
          </form>

    );
  props.toggleDrawer();
}



      return (
        <CalendarList

        dayComponent={({date, state}) => {
          return (

              <div onClick={() => {editDay(date)}} style={{textAlign: 'center'}}>{getDayData(date)}</div>

          );
        }}

  theme={{
    backgroundColor: '#ccc',
    'stylesheet.calendar.main': {
      dayContainer: {
        borderBottomWidth:'2px',
        borderBottomColor:'black',
        borderTopWidth:'2px',
        borderTopColor:'black',
        borderLeftWidth:'1px',
        borderLeftColor:'black',
        borderRightWidth:'1px',
        borderRightColor:'black',
        flex: 1,
        alignItems: 'center',
        height:'60px'

      }
    }
  }}


         // Callback which gets executed when visible months change in scroll view. Default = undefined
   onVisibleMonthsChange={(months) => {console.log('now these months are visible', months);}}
   // Max amount of months allowed to scroll to the past. Default = 50
   pastScrollRange={3}
   // Max amount of months allowed to scroll to the future. Default = 50
   futureScrollRange={2}
   // Enable or disable scrolling of calendar list
   scrollEnabled={true}
   // Enable or disable vertical scroll indicator. Default = false
   showScrollIndicator={true}

 />
          )
      }
