import React, { useState,useEffect } from 'react';
import axios from 'axios';
import { Alert, StyleSheet, Text, Pressable, View } from "react-native";
import Modal from "react-native-modal";


export default function Tutorial(props) {

  //alcohol
  const [drinkValue, setDrinkValue] = useState('');

  //workout
  const [workOut, setWorkOut] = useState('');

  //diet
  const [followedDiet, setFollowedDiet] = useState(true);
  const [fastEnded, setFastEnded] = useState(false);

  const styles = StyleSheet.create({
    centeredView: {

      justifyContent: "center",
      alignItems: "center",
      marginTop: 22
    },
    modalView: {
      margin: 20,
      backgroundColor: "white",
      borderRadius: 20,
      padding: 35,
      alignItems: "center",
      shadowColor: "#000",
      shadowOffset: {
        width: 0,
        height: 2
      },
      shadowOpacity: 0.25,
      shadowRadius: 4,
      elevation: 5
    },
    button: {
      borderRadius: 20,
      padding: 10,
      elevation: 2
    },
    buttonOpen: {
      backgroundColor: "#F194FF",
    },
    buttonClose: {
      backgroundColor: "#2196F3",
    },
    textStyle: {
      color: "white",
      fontWeight: "bold",
      textAlign: "center"
    },
    modalText: {
      marginBottom: 15,
      textAlign: "center"
    }
  });

  function submitNewData(date) {
    if(props.calType==='alcohol')
        var json = ({
          activity_type: "alcohol",
          date: date,
          activity_data: {
            alcoholDrinksHad: drinkValue
          }
        });
    else if(props.calType==='diet')
        var json = ({
          activity_type : "diet",
          date : date,
          activity_data : {
              followedDiet : followedDiet,
              fastEnded:fastEnded
          }
          });
    else if(props.calType==='workout')
        var json = ({
          activity_type : "workout",
          date : date,
          activity_data : {
              workOut : workOut
          }
          });

    console.log(JSON.stringify(json))
    var url;
    if (process.env.NODE_ENV == 'production')
      url = '';
    else
      url = 'http://localhost:3000'
    axios.post(url + '/Activity/edit', json, { withCredentials: true }, {
      headers: { 'Content-Type': 'application/json',"Access-Control-Allow-Origin": "*" }
    })
      .then(
        setTimeout(() => {
          props.updateData()
        }, 500)
      ).catch(function (error) {
        console.log(error);
      });
    props.closeModal();

  }





var tutorialContent = [ <View style={styles.modalView}>
    <form onChange={e => setDrinkValue(e.target.value)}>
      <div>Welcome to Health Tracker! Here is a quick overview of how to use the app.</div>
      <input type="text" inputprops={{ inputMode: 'numeric' }} value={drinkValue} onChange={e => setDrinkValue(e.target.value)}/>


    </form>
    <br></br>
    <Pressable
      style={[styles.button, styles.buttonClose]}
      onPress={() => submitNewData(props.clickedDate)}
    >
      <Text style={styles.textStyle}>Save</Text>
    </Pressable>
  </View>];


      // NOTE: You also need to provide styles, see https://github.com/negomi/react-burger-menu#styling
      return (
           <Modal
        animationType="slide"
        onBackdropPress={() => {props.closeModal() }}
        transparent={true}
        visible={true}
        onRequestClose={() => {
          props.closeModal()
        }}

      >
        <View style={styles.centeredView}>
            {tutorialContent[0]}

        </View>
      </Modal>
      );

  }
