  state = {
    view:'submitPhone',
    phone:null,
    hash:null

  };

  handlePhoneSubmit(phone,hash) {
         this.setState({hash:hash});
        this.setState({phone:phone});
        this.setState({view:'submitOTP'});
  }

  componentDidMount() {



  }


  render() {
    return (<div> 
        {(this.state.view==='submitPhone'?


        <SubmitPhone></SubmitPhone> :
        <VerifyOTP></VerifyOTP>
        )}
    </div>

    );
  }
}
